import React, { useState, useEffect } from 'react';
// import logo from './logo.svg';
import './App.css';


const App = () => { 
  const [covid, setCovid] = useState([]);
  const [countryName, setCountryName] = useState('Japan');
  const [url, setUrl] = useState('https://covid-api-v1-2.herokuapp.com/covidUpdate/api/v1?country=Japan');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
   const fetchCovid = () => {
      setLoading(true);
      fetch(url)
      .then(result => result.json())
      .then(data => ( setCovid(data.covid) && setLoading(false) ) )
      .catch(error => console.log(error));
   };
    fetchCovid();
   }, [url]);

  const handleOnclick = e => {
   e.preventDefault();
   setCountryName(e.target.value);
   setUrl(`https://covid-api-v1-2.herokuapp.com/covidUpdate/api/v1?country=${countryName}`);
  };

  return (
     <div>
        <h2>Test</h2>
        {loading ? <h2>Loading....</h2> : ""}
        <ul>
           <li><button value = 'Japan' onClick = {handleOnclick}>Japan</button></li>
           <li><button value = 'Philippines' onClick = {handleOnclick}>Philippines</button></li>
        </ul>
        <ul>
           <li>{covid.TotalPositive}</li>
           <li>{covid.TotalDeath}</li>
           <li>{covid.TotalRecovered}</li>
        </ul>
     </div>

  );

};

export default App;
