import React from "react";
import { Line } from "react-chartjs-2";
import moment from "moment-timezone";

const CovidLineChart = ({ covidDetails }) => {
  const currentYear = moment().format("YYYY");
  const currentMonth = moment().format("MMM");

  const { Latest } = covidDetails;

  const positiveArray = [];
  const deathArray = [];
  const recoveriesArray = [];

  for (let latests of Latest) {
    positiveArray.push(latests.covid_positive);
    deathArray.push(latests.covid_death);
    recoveriesArray.push(latests.covid_recovered);
  }

  const graphDetails = {
    labels: getDays(currentMonth, currentYear),
    datasets: [
      {
        label: "Positives",
        fill: false,
        lineTension: 0.5,
        backgroundColor: "#f2f2f2",
        borderColor: "#FF8C00",
        borderWidth: 1,
        data: positiveArray,
      },
      {
        label: "Deaths",
        fill: false,
        lineTension: 0.5,
        backgroundColor: "#000000",
        borderColor: "#B22222",
        borderWidth: 1,
        data: deathArray,
      },
      {
        label: "Recoveries",
        fill: false,
        lineTension: 0.5,
        backgroundColor: "#000000",
        borderColor: "#006400",
        borderWidth: 1,
        data: recoveriesArray,
      },
    ],
  };
  return (
    <div style="position:relative">
      <Line
        data={graphDetails}
        options={{
          title: {
            display: true,
            text: "Covid Case Graph Per Day",
            fontSize: 20,
          },
          legend: {
            display: true,
            position: "top",
          },
        }}
      />
    </div>
  );
};

const getDays = (month, year) => {
  const dateArray = [];
  const monthYear = moment(year + "-" + month, "YYYY-MMM");
  for (
    const end = moment(monthYear).add(1, "month");
    monthYear.isBefore(end);
    monthYear.add(1, "day")
  ) {
    dateArray.push(monthYear.format("D-ddd"));
  }
  return dateArray;
};

export default CovidLineChart;
