import React from "react";
import { Line } from "react-chartjs-2";
const moment = require("moment-timezone");

function getMonths(month, year) {
  var ar = [];
  var start = moment(year + "-" + month, "YYYY-MMM");
  for (
    var end = moment(start).add(1, "month");
    start.isBefore(end);
    start.add(1, "day")
  ) {
    ar.push(start.format("D-ddd"));
  }
  return ar;
}
// console.log(getMonths('Mar',2011))

const state = {
  labels: [
    "January",
    "February",
    "March",
    "April",
    "May",
    "May",
    "May",
    "May",
    "May",
    "May",
    "May",
    "May",
    "May",
    "May",
  ],
  datasets: [
    {
      label: "Positives",
      fill: false,
      lineTension: 0.5,
      backgroundColor: "#f2f2f2",
      borderColor: "#FF8C00",
      borderWidth: 1,
      data: [65, 59, 80, 81, 56],
    },
    {
      label: "Deaths",
      fill: false,
      lineTension: 0.5,
      backgroundColor: "#000000",
      borderColor: "#B22222",
      borderWidth: 1,
      data: [1, 1, 80, 81, 56],
    },
    {
      label: "Recoveries",
      fill: false,
      lineTension: 0.5,
      backgroundColor: "#000000",
      borderColor: "#006400",
      borderWidth: 1,
      data: [1, 1, 80, 50, 1000],
    },
  ],
};

const CovidLineChart = () => {
  console.log(getMonths("Mar", 2011));
  console.log(moment().format("YYYY"));
  console.log(moment().format("MMM"));
  return (
    <div>
      <Line
        data={state}
        options={{
          title: {
            display: true,
            text: "Covid Case Graph",
            fontSize: 20,
          },
          legend: {
            display: true,
            position: "top",
          },
        }}
      />
    </div>
  );
};

export default CovidLineChart;
