import React from "react";
import covidStyle from "../../styles/content/covidStyle";

// let className = 'menu';
// if (this.props.isActive) {
//   className += ' menu-active';
// }
// return <span className={className}>Menu</span>

const CountryButton = ({ clickEventCountry, countryProp }) => {
  let className = "menu";

  if (countryProp === "Japan") {
    className += " menu-active";
  }

  console.log(countryProp);

  return (
    <div style={covidStyle.divStyle}>
      <ul style={covidStyle.bListUlStyle}>
        <li className={className} style={covidStyle.bListliStyle}>
          <button
            style={covidStyle.buttonStyle}
            value="Japan"
            onClick={clickEventCountry}
          >
            Japan
          </button>
        </li>
        <li className={className} style={covidStyle.bListliStyle}>
          <button
            style={covidStyle.buttonStyle}
            value="Philippines"
            onClick={clickEventCountry}
          >
            Philippines
          </button>
        </li>
      </ul>
    </div>
  );
};

export default CountryButton;
