import React, { useState, useEffect, useLayoutEffect } from "react";
import CountryButton from "./CountryButton";
import ContentApiDetails from "./ContentApiDetails";

const CovidPage = () => {
  const [covid, setCovid] = useState([]);
  const [countryName, setCountryName] = useState("Japan");
  const [loading, setLoading] = useState(false);
  const url = `https://covid-api-v1-2.herokuapp.com/covidUpdate/api/v1?country=${countryName}`;

  console.log(`${covid} 1`);
  useLayoutEffect(() => {
    const fetchCovid = () => {
      setLoading(true);
      fetch(url)
        .then((result) => result.json())
        .then((data) => setCovid(data.covid) || setLoading(false))
        .catch((error) => console.log(error));
    };
    fetchCovid();
    console.log(`${covid} 2`);
  }, [url]);

  const handleOnclick = (e) => {
    setCountryName(e.target.value);
    e.preventDefault();
  };
  console.log(`${covid} 4`);

  const { Latest } = covid;
  // const [{ country, covid_positive }] = Latest;

  // console.log(Latest);
  return (
    <div>
      {console.log(`${covid} 5`)}
      <CountryButton clickEventCountry={handleOnclick} />
      <ContentApiDetails covidDetails={covid} loadingIcon={loading} />
    </div>
  );
};

export default CovidPage;
