import React from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const ContentApiDetails = ({ covidDetails, loadingIcon }) => {
  const { TotalPositive, TotalDeath, TotalRecovered } = covidDetails;
  return (
    <div>
      <ul>
        <li>
          Favorite beverage: <FontAwesomeIcon icon="blender" />
        </li>
        <li>{loadingIcon ? <h2>Loading....</h2> : TotalPositive}</li>
        <li>{loadingIcon ? <h2>Loading....</h2> : TotalDeath}</li>
        <li>{loadingIcon ? <h2>Loading....</h2> : TotalRecovered}</li>
      </ul>
    </div>
  );
};

export default ContentApiDetails;
