import React from "react";
import IconList from "../Icon/IconList";
import ContentDisplay from "./ContentDisplay";
import covidStyle from "../../styles/content/covidStyle";

const ContentApiDetails = ({ covidDetails, loadingIcon }) => {
  const { TotalPositive, TotalDeath, TotalRecovered, Latest } = covidDetails;
  const { IconSpinner, IconPositive, IconRecovery, IconDeath } = IconList;
  // if(Object.keys(Latest).length !== 0){
  //   const [
  //     { country, covid_positive, covid_death, covid_recovered, covid_date },
  //   ] = Latest;
  // }
  // const [
  //   { country, covid_positive, covid_death, covid_recovered, covid_date },
  // ] = Latest;

  // Object.keys(Latest).length !== 0 ? console.log(true) : console.log(false);

  return (
    <div>
      <h1 style={covidStyle.h1Style}>COVID cases in {country}</h1>
      <h5 style={covidStyle.h5Style}>Last updated: {covid_date}</h5>
      <ul>
        <li style={covidStyle.dataDetailStyle}>
          <h3 style={covidStyle.h3Style}>
            <IconPositive />
            Positives
          </h3>
          {loadingIcon && Object.keys(Latest).length ? (
            <IconSpinner />
          ) : (
            <ContentDisplay
              totalData={TotalPositive}
              singleData={Latest.covid_positive}
            />
          )}
        </li>
        <li style={covidStyle.dataDetailStyle}>
          <h3 style={covidStyle.h3Style}>
            <IconDeath />
            Deaths
          </h3>
          {loadingIcon && Object.keys(Latest).length ? (
            <IconSpinner />
          ) : (
            <ContentDisplay
              totalData={TotalDeath}
              singleData={Latest.covid_death}
            />
          )}
        </li>
        <li style={covidStyle.dataDetailStyle}>
          <h3 style={covidStyle.h3Style}>
            <IconRecovery />
            Recoveries
          </h3>
          {loadingIcon && Object.keys(Latest).length ? (
            <IconSpinner />
          ) : (
            <ContentDisplay
              totalData={TotalRecovered}
              singleData={Latest.covid_recovered}
            />
          )}
        </li>
      </ul>
    </div>
  );
};

export default ContentApiDetails;
