import React from "react";
import { Line } from "react-chartjs-2";
import moment from "moment-timezone";

const CovidLineChart = () => {
  const currentYear = moment().format("YYYY");
  const currentMonth = moment().format("MMM");
  const state = {
    labels: getDays(currentMonth, currentYear),
    datasets: [
      {
        label: "Positives",
        fill: false,
        lineTension: 0.5,
        backgroundColor: "#f2f2f2",
        borderColor: "#FF8C00",
        borderWidth: 1,
        data: [65, 59, 80, 81, 56],
      },
      {
        label: "Deaths",
        fill: false,
        lineTension: 0.5,
        backgroundColor: "#000000",
        borderColor: "#B22222",
        borderWidth: 1,
        data: [1, 1, 80, 81, 56],
      },
      {
        label: "Recoveries",
        fill: false,
        lineTension: 0.5,
        backgroundColor: "#000000",
        borderColor: "#006400",
        borderWidth: 1,
        data: [1, 1, 80, 50, 1000],
      },
    ],
  };
  return (
    <div>
      <Line
        data={state}
        options={{
          title: {
            display: true,
            text: "Covid Case Graph Per Day",
            fontSize: 20,
          },
          legend: {
            display: true,
            position: "top",
          },
        }}
      />
    </div>
  );
};

const getDays = (month, year) => {
  const dateArray = [];
  const monthYear = moment(year + "-" + month, "YYYY-MMM");
  for (
    const end = moment(monthYear).add(1, "month");
    monthYear.isBefore(end);
    monthYear.add(1, "day")
  ) {
    dateArray.push(monthYear.format("D-ddd"));
  }
  return dateArray;
};

export default CovidLineChart;
