import React from "react";
import IconList from "../Icon/IconList";
import ContentDisplay from "./ContentDisplay";
import covidStyle from "../../styles/content/covidStyle";

const ContentApiDetails = ({ covidDetails, loadingIcon }) => {
  const { TotalPositive, TotalDeath, TotalRecovered, Latest } = covidDetails;
  const { IconSpinner, IconPositive, IconRecovery, IconDeath } = IconList;

  const latestDate = Latest[Latest.length - 1];

  // console.log(Latest[Latest.length - 1]);

  const {
    country,
    covid_positive,
    covid_death,
    covid_recovered,
    covid_date,
  } = latestDate;

  console.log(latestDate);

  return (
    <div>
      <h1 style={covidStyle.h1Style}>
        COVID cases in
        {/* {country} */}
      </h1>
      <h5 style={covidStyle.h5Style}>
        Last updated:
        {/* {covid_date} */}
      </h5>
      <ul>
        <li style={covidStyle.dataDetailStyle}>
          <h3 style={covidStyle.h3Style}>
            <IconPositive />
            Positives
          </h3>
          {loadingIcon ? (
            <IconSpinner />
          ) : (
            <ContentDisplay
              totalData={TotalPositive}
              // singleData={covid_positive}
            />
          )}
        </li>
        <li style={covidStyle.dataDetailStyle}>
          <h3 style={covidStyle.h3Style}>
            <IconDeath />
            Deaths
          </h3>
          {loadingIcon ? (
            <IconSpinner />
          ) : (
            <ContentDisplay
              totalData={TotalDeath}
              // singleData={covid_death}
            />
          )}
        </li>
        <li style={covidStyle.dataDetailStyle}>
          <h3 style={covidStyle.h3Style}>
            <IconRecovery />
            Recoveries
          </h3>
          {loadingIcon ? (
            <IconSpinner />
          ) : (
            <ContentDisplay
              totalData={TotalRecovered}
              // singleData={covid_recovered}
            />
          )}
        </li>
      </ul>
    </div>
  );
};

export default ContentApiDetails;
