//ContentApiDetails
const h3Style = {
  fontSize: "25px",
  backgroundColor: "rgb(240, 255, 255)",
  color: "rgb(31, 45, 61)",
  padding: "10px 0",
  margin: 0,
};
const h5Style = {
  fontSize: "15px",
  color: "rgb(31, 45, 61)",
  padding: "10px 10px",
  margin: 0,
  fontStyle: "italic",
  fontWeight: 400,
};
const h1Style = {
  fontSize: "40px",
  color: "rgb(31, 45, 61)",
  padding: "10px 0",
  margin: 0,
  textAlign: "center",
};
const dataDetailStyle = {
  display: "inline-block",
  width: "31%",
  border: "1px solid #000000",
  textAlign: "center",
  margin: "0 10px",
};

//CountryButton
const bListliStyle = {
  display: "inline-block",
  paddingRight: "15px",
};
const divStyle = {
  borderBottom: "1px solid rgb(0, 0, 0)",
};

const buttonStyle = {
  background: "none",
  borderWidth: "1px",
  borderStyle: "solid",
  // borderColor: "rgb(0, 0, 0) rgb(0, 0, 0)",
  padding: "7px 30px",
  // borderBottom: "1px solid #f2f2f2",
};

const bListUlStyle = {
  marginBottom: "-1px",
};

//ContentDisplay
const posStyle = {
  marginRight: "12px",
  color: "#FF8C00",
};
const deStyle = {
  marginRight: "12px",
  color: "#B22222",
};
const recStyle = {
  marginRight: "12px",
  color: "#006400",
};

export default {
  buttonStyle,
  bListliStyle,
  dataDetailStyle,
  bListUlStyle,
  divStyle,
  h1Style,
  h5Style,
  h3Style,
  posStyle,
  deStyle,
  recStyle,
};
