import React, { useState, useEffect } from "react";
// import CountryButton from "./CountryButton";
// import ContentApiDetails from "./ContentApiDetails";
import { IconSpinner } from "../Icon/IconList";
import DataContext from "../context/DataContext";

const ApiCovidFetch = (props) => {
  const [covid, setCovid] = useState({});
  const [countryName, setCountryName] = useState("Japan");
  const [loading, setLoading] = useState(false);
  const url = `https://covid-api-v1-2.herokuapp.com/covidUpdate/api/v1?country=${countryName}`;

  useEffect(() => {
    const options = {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json;charset=UTF-8",
      },
    };
    const fetchCovid = () => {
      setLoading(true);
      fetch(url, options)
        .then((result) => result.json())
        .then((data) => {
          setCovid(data.covid) || setLoading(false);
        })
        .catch((error) => console.log(error));
    };
    fetchCovid();
  }, [url]);

  const handleOnclick = (e) => {
    e.preventDefault();
    setCountryName(e.target.value);
  };

  const spinStyle = {
    display: "block",
    textAlign: "center",
    fontSize: "100px",
    marginTop: "60px",
  };

  console.log(covid.Latest[covid.Latest.length - 1]);

  return Object.keys(covid).length === 0 ? (
    <IconSpinner propStyle={spinStyle} />
  ) : (
    <DataContext.Provider
      value={{
        covidDetails: covid,
        loading,
        buttonClickEvent: { handleOnclick },
      }}
    >
      {props.children}
    </DataContext.Provider>
  );
};

export default ApiCovidFetch;
