import React, { Component, useState, useEffect, useCallback } from 'react';
// import logo from './logo.svg';
import './App.css';


const App = () => {
  // const [ count, setCount ] = useState(0);

  const [covid, setCovid] = useState([]);

  const fetchCovid = () => {
     fetch("https://covid-api-v1-2.herokuapp.com/covidUpdate/api/v1?country=Philippines")
     //fetch("https://hn.algolia.com/api/v1/search?query=react") 
     .then(result => result.json())
     .then(data => setCovid(data.covid))
     .catch(error => console.log(error));
  };

  useEffect(() => {
    fetchCovid();
  });

  return (
     <div>
        <h2>Test</h2>
        <ul>
           <li>{covid.TotalPositive}</li>
           <li>{covid.TotalDeath}</li>
           <li>{covid.TotalRecovered}</li>
        </ul>
     </div>

  );

  // document.title = `${count}`;

  // const increment = () => {
  //   setCount(count + 1);
  // };

  // return (
  //  <div>
  //    <h2>counter app</h2>

  //    <button onClick = {increment}>Click {count} times</button>
  //  </div>
  // );

};

export default App;
